import React from "react";
import DropdownItem from "../DropdownItem";
import faker from "faker";

// Here we create a dropdown button where parents will select their student
// using one of the DropdownItem button components inside the dropdown menu

function Dropdown() {
    return (
        <div className="ui selection dropdown">
            <input type="hidden" name="student" />
            <i className="dropdown icon"></i>
            <div className="default text">Select Child</div>
            <div className="menu">
                <div className="item" >
                <img className="ui mini avatar image" imageSource={faker.image.avatar()} alt="student avatar" />
                {faker.name.firstName()}
            </div>
            </div>
        </div>
    )
}

export default Dropdown;
